// program for size of variable //
#include <iostream>
using namespace std;
int main()
{
    // it is a function of size of datatype
    cout << " The size of int           is  : " << sizeof(int) << " bytes" << endl;
    cout << " The size of long int      is  : " << sizeof(long int) << " bytes" << endl;
    cout << " The size of flaot         is  : " << sizeof(float) << " bytes" << endl;
    cout << " The size of char          is  : " << sizeof(char) << " bytes" << endl;
    cout << " The size of double        is  : " << sizeof(double) << " bytes" << endl;
    cout << " The size of long double   is  : " << sizeof(long double) << " bytes" << endl;

    return 0;
}